class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long res=0;
        vector<long long> arr(n);   
        for (int i=0; i<n; ++i) 
        {
            arr[i]=target[i]-nums[i];
        }
        long long temp=0;
        for (int i=0; i<n; ++i) 
        {
            long long currDiff=arr[i];
            if (i==0 || (temp>0 && currDiff>0) || (temp<0 && currDiff<0)) 
            {
                long long op=abs(currDiff)-abs(temp);
                if (op>0) 
                {
                    res+=op;
                }
                temp=currDiff;
            } else 
            {
                res+=abs(currDiff);
                temp=currDiff;
            }
        }

        return res;
    }
};
