class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [target[i] - nums[i] for i in range(n)]
        res = abs(diff[0])
        curr = diff[0]

        for i in range(1, n):
            if  (diff[i - 1] < 0 and diff[i] < 0) or (diff[i - 1] > 0 and diff[i] > 0):
                op = abs(diff[i]) - abs(curr)
                if op > 0:
                    res += op     
            else:
                res += abs(diff[i])
            curr = diff[i]


        return res
