class Solution {
public:
    long long minimumOperations(vector<int>& numsx, vector<int>& targetx) {
        vector<int> diffx(numsx.size());
        for(size_t xindexx =0;xindexx<numsx.size();++xindexx) {
            diffx[xindexx]=targetx[xindexx]-numsx[xindexx];
        }

        long long totalx_operationsx=0;
        long long currentx_segmentx_diffx=0;
   for (size_t xindexx = 0; xindexx < diffx.size(); ++xindexx) {
            if (xindexx == 0 || (diffx[xindexx - 1] > 0 && diffx[xindexx] > 0) || (diffx[xindexx - 1] < 0 && diffx[xindexx] < 0)) {
                int additionalx_operationsx = abs(diffx[xindexx]) - abs(currentx_segmentx_diffx);
                if (additionalx_operationsx > 0) {
                    totalx_operationsx += additionalx_operationsx;
                }
                currentx_segmentx_diffx = diffx[xindexx];
            } else {
                totalx_operationsx += abs(diffx[xindexx]);
                currentx_segmentx_diffx = diffx[xindexx];
            }
        }

        return totalx_operationsx;
    }
};
