class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long>d(n);

        for(int i = 0 ; i < n; i++)   
        d[i] = nums[i]-target[i];

        long long o = 0;
        long long b = 0;
        for(int i = 0; i < d.size() ; i++)
        {
            if(i==0 || (d[i-1]>0 && d[i]>0) || (d[i-1]<0 && d[i]<0)) {
            int ao = abs(d[i]) - abs(b);
            if(ao>0)
            o+=ao;

            b=d[i];
            }
            else
            {
                o+=abs(d[i]);
                b=d[i];
            }
        }
          return o;
    }
};
