class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
            int n=nums.size();
           vector<int> diff(n);
        for (int i = 0; i < nums.size(); ++i) {
            diff[i] = target[i] - nums[i];
        }

        long long total=0;
        long long current=0;

        for (int i = 0; i<diff.size(); i+=1) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int x=abs(diff[i])-abs(current);
                if (x> 0) {
                    total+=x;
                }
                current=diff[i];
            } 
                else {
                total+=abs(diff[i]);
                current=diff[i];
            }
        }

        return total;


    }
};
