class Solution {
public:
long long minimumOperations(vector<int> &nums, vector<int> &target) {
    nums.push_back(0);
    target.push_back(0);
    int n = nums.size();
    long long ans = 0;
    queue<vector<int>> q;
    vector<int> a;
    for (int i = 0; i < n; i++) {
        nums[i] -= target[i];
        if (a.size() && (nums[i] == 0 || nums[i] > 0 && a.back() < 0 || nums[i] < 0 && a.back() > 0)) {
            for (int &num : a)
                num = abs(num);
            q.push(a);
            a.clear();
        }
        if (nums[i])
            a.push_back(nums[i]);
    }

    while (q.size()) {
        vector<int> b = q.front();
        q.pop();

        int minO = INT_MAX;
        for (int num : b)
            minO = min(minO, num);
        ans += minO;
        a.clear();

        for (int num : b) {
            num -= minO;
            if (num == 0 && a.size()) {
                q.push(a);
                a.clear();
            }
            if (num)
                a.push_back(num);
        }
        if (a.size())
            q.push(a);
    }

    return ans;
}
};
