/**
 * @param {number[]} nums
 * @param {number[]} target
 * @return {number}
 */
var minimumOperations = function(nums, target) {
    let n = nums.length;
    let diff = new Array(n).fill(0);
    let res = [];
    let flag = false;
    let base = 0;
    for(let i=0;i<n;i++) {
        let temp = false;
        diff[i]=target[i] - nums[i];
        if(i === 0) {
            base = 0;
            res.unshift(diff[i]);
            temp = true;
        } else if(diff[i] >= 0) {
            if(diff[i-1] >= 0) {
                if(diff[i] >= diff[i-1]) {
                    if(flag) {
                        res[0] = diff[i] - base;
                        temp = true;
                    } else {
                        res.unshift(diff[i] - base);
                        temp = true;
                    }
                } else {
                    base = diff[i];
                }
            } else {
                base = 0;
                res.unshift(diff[i]);
                temp = true;
            }
        } else if(diff[i] < 0) {
            if(diff[i-1] < 0) {
                if(diff[i] <= diff[i-1]) {
                    if(flag) {
                        res[0] = diff[i] - base;
                        temp = true;
                    } else {
                        res.unshift(diff[i]-base);
                        temp = true;
                    }
                } else {
                    base = diff[i];
                }
            } else {
                base = 0;
                res.unshift(diff[i]);
                temp = true;
            }
        }
        flag = temp;
    }
    return res.reduce((pre,cur)=>pre+Math.abs(cur), 0);
};
