class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> dif(n);
        for (int i=0 ; i<n; i++) dif[i]= target[i]-nums[i] ;
        int prev = dif[0];
        long long ans = abs(dif[0]);

        for (int i=1 ; i<n; i++)
        {
            if ((prev>0 && dif[i]>0 ) or (prev<0 && dif[i]<0))
            {
                if (abs(prev)<abs(dif[i]))
                {
                    ans+= abs(dif[i])-abs(prev);
                }
            }
            else ans += abs(dif[i]);
            prev=dif[i];
        }
        return ans;

    }
};
