class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        int n=t.size();
        vector<int>pd(n),nd(n,0);
        for(int i=0;i<n;i++){
            if(t[i]-nums[i]<0) nd[i]=abs(t[i]-nums[i]);
            else pd[i]=abs(t[i]-nums[i]);
        }
        // for(int z:pd){
        //     cout<<z<<" ";
        // }
        // cout<<endl;
        //  for(int z:nd){
        //     cout<<z<<" ";
        // }
        // cout<<endl;
        long long ans=0;
        int i=0;
        while(i<n){
            if(pd[i]==0){
                i++;
                continue;
            }
            vector<long long>v;
            while(i<n && pd[i]!=0){
                v.push_back(pd[i]);
                i++;
            }
            vector<long long>diff=v;
            for(int x=1;x<v.size();x++){
                diff[x]=v[x]-v[x-1];
            }
            long long res=0;
            for(int x:diff){
                if(x>0) res+=(long long)x;
            }
            ans+=res;
            i++;
        }
        i=0;
        while(i<n){
            vector<long long>v;
            while(i<n && nd[i]!=0){
                v.push_back(nd[i]);
                i++;
            }
            vector<long long>diff=v;
            for(int x=1;x<v.size();x++){
                diff[x]=v[x]-v[x-1];
            }
            long long res=0;
            for(int x:diff){
                if(x>0) res+=(long long)x;
            }
            ans+=res;
            i++;
        }
        return ans;
    }
};
