class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n+1,0);
        for(int i = 0; i < n; i++){
            diff[i+1] = target[i] - nums[i];
        }
        long long ans = 0;
        int b = 0,pre = 0;
        for(int i = 1; i <= n; i++){
            if(diff[i] >= 0){
                b = diff[i] - pre;
                if(b > 0)   ans += b;
                pre = diff[i];
            }
            else{
                pre = 0;
            }
        }
        // cout << ans <<endl;
        pre = 0;
        for(int i = 1; i <= n; i++){
            if(diff[i] <= 0){
                b = -diff[i] - pre;
                if(b > 0)   ans += b;
                pre = -diff[i];
            }
            else{
                pre = 0;
            }
        }
        return ans;
    }
};
