class Solution {
public:
  long long minimumOperations(vector<int>& nums, vector<int>& target) {
    for (int i = 0; i < target.size(); i++) nums[i] -= target[i];
    vector<int> ss(nums.size(), 0);
    ss[0] = nums[0];
    for (int i = 1; i < nums.size(); i++) {
      ss[i] = nums[i] - nums[i - 1];
    }
    long long r1 = 0, r2 = 0;
    for (int i = 0; i < ss.size(); i++) {
      if (ss[i] > 0) {
        r1 += ss[i];
      }
      else {
        r2 -= ss[i];
      }
    }

    return max(r1, r2);
  }
};
