class Solution {
public:
    long long minimumOperations(vector<int>&a , vector<int>& b) {
       long long c = a.size();
        vector<long long> d(c);

        for (int e = 0; e < c; ++e) {
            d[e] = b[e] - a[e];
        }

        long long f = 0;
        long long g = 0;

        for (int h = 0; h < c; ++h) {
            if (h == 0 || (d[h - 1] > 0 && d[h] > 0) || (d[h - 1] < 0 && d[h] < 0)) {
                int i = abs(d[h]) - abs(g);
                if (i > 0) {
                    f += i;
                }
                g = d[h];
            } else {
                f += abs(d[h]);
                g = d[h];
            }
        }

        return f;
    }
};
