class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans {} ;
        int n {(int)nums.size()} ;
        vector<int> diff (n , 0) ;
        for(int i {} ;  i < n ; i++){
            diff[i] = target[i] -nums[i] ;
            // cout << diff[i] << " "  ;
        }
        // cout << endl ;
        int i {} ;
        while(i < n){

            int maxi {INT_MIN }, mini {INT_MAX} ;

            int j {i} ;

            while(j < n && diff[j] > 0){
                mini = min(diff[j] , mini) ;
                j++ ;
            }
            int k {i} ;
            while(k < j ){
                diff[k++] -= mini ;
            }
            if(mini != INT_MAX)
            ans += mini ;


            int l {i} ;
            while( l < n && diff[l] < 0){
                maxi = max(diff[l] , maxi) ;
                l++ ;
            }
            // cout << maxi << " " << i << " " <<  l << endl ;
            int m {i} ;

            while(m < l){
                diff[m++] -= maxi ;
            }


            if(maxi != INT_MIN)
            ans -= maxi ;

            while(i < n && diff[i] == 0) i++ ;


        }

        return ans ;
    }
};

