class Solution {
public:
    long long help(int& prev, int& diff, int& st, long long& ans, int num, int target) {
        if (num < target) {
            if (target - num > diff) {
                st = 1;
                diff = target - num;
            } else {
                ans += static_cast<long long>(diff - target + num);
                diff = target - num;
            }
        } else if (num > target) {
            if (num - target > diff) {
                st = 1;
                diff = num - target;
            } else {
                ans += static_cast<long long>(diff + target - num);
                diff = num - target;
            }
        }
        return diff;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int prev = 0,diff = 0,st = 0;
        long long ans = 0;
        int n = nums.size();

        for (int i = 0; i < n; ++i) {
            if (prev == 0) {
                if (nums[i] < target[i]) {
                    prev = 1;
                    diff = target[i] - nums[i];
                    st += 1;
                } else if (nums[i] > target[i]) {
                    prev = -1;
                    diff = nums[i] - target[i];
                    st += 1;
                } else {
                    continue;
                }
            } else {
                if ((nums[i] < target[i] && prev == 1 && diff == target[i] - nums[i]) ||
                    (nums[i] > target[i] && prev == -1 && diff == nums[i] - target[i])) {
                    st += 1;
                    continue;
                } else if (nums[i] < target[i] && prev == 1) {
                    diff = help(prev, diff, st, ans, nums[i], target[i]);
                    continue;
                } else if (nums[i] > target[i] && prev == -1) {
                    diff = help(prev, diff, st, ans, nums[i], target[i]);
                    continue;
                } else if (nums[i] == target[i]) {
                    ans += static_cast<long long>(diff);
                    prev = 0;
                    st = 0;
                    diff = 0;
                } else {
                    ans += static_cast<long long>(diff);
                    if (nums[i] < target[i]) {
                        prev = 1;
                        diff = target[i] - nums[i];
                        st += 1;
                    } else {
                        prev = -1;
                        diff = nums[i] - target[i];
                        st += 1;
                    }
                }
            }
        }

        return ans + static_cast<long long>(diff);
    }
};
