class Solution {
public:
    long long find(vector<int> temp){
        long long ans = temp[0];
        for(int i=0;i<temp.size()-1;i++){
            if(temp[i+1]>temp[i]) ans+=temp[i+1]-temp[i];
        }
        return ans;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        for(int i=0;i<n;i++){
            nums[i]=target[i]-nums[i];
        }

        int i=0;
        while(i<n){
            vector<int> temp={};
            if(nums[i]<0){
                while(i<n && nums[i]<0){
                    if(!temp.size() || nums[i]!=temp[temp.size()-1]) temp.push_back(abs(nums[i]));
                    i++;
                } 
                ans+=find(temp);
            }
            else if(nums[i]>0){
                while(i<n && nums[i]>0){
                    if(!temp.size() || nums[i]!=temp[temp.size()-1]) temp.push_back(abs(nums[i]));
                    i++;
                }
                ans+=find(temp);
            }
            else i++;
        }
        return ans;
    }
};
