class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long min_cnt=0;
        long long max_cnt=0;
        long long ans=0;
        for(int i=n-1;i>=0;i--)
        {
            if(i==n-1)
            {
                if((target[i]-nums[i])>=0)
                {
                    max_cnt=(target[i]-nums[i]);
                    min_cnt=0;
                }
                else 
                 {min_cnt=target[i]-nums[i];
                  max_cnt=0;
                 }
                ans+=abs(target[i]-nums[i]);
                continue;
            }
             if((target[i]-nums[i])>=0)
                {
                    long long cnt=target[i]-nums[i];
                     if(max_cnt<cnt)
                     {
                         ans+=min(cnt,cnt-max_cnt);

                     }
                    max_cnt=cnt;
                    min_cnt=0;
                }
                else 
                 { 
                    long long cnt=target[i]-nums[i];
                    if(cnt<min_cnt)
                    {
                        ans+=min(abs(cnt),abs(cnt-min_cnt));
                    }

                    min_cnt=cnt;
                    max_cnt=0;
                 }


        }
        return ans;
    }
};
