class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n=len(nums)
        diff=[0]*n
        def find(x,y):
            return abs(x)-abs(y)
        for i in range(n):
            diff[i]=nums[i]-target[i]
        cur=diff[0]
        ans=abs(diff[0])
        for i in range(1,len(nums)):
            if (cur>0 and diff[i]>0) or(cur<0 and diff[i]<0):
                x=find(diff[i],cur)
                if x>0: ans+=x
            else:
                ans+=abs(diff[i])
            cur=diff[i]
        return ans
