class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long oprns = 0, incremented = 0, decremented = 0, n = nums.size();
        for(int i=0; i<n; i++){
            if(nums[i] != target[i]){
                if(nums[i]<target[i]){ //increment
                    decremented = 0;
                    int diff = target[i]-nums[i];
                    if(diff>incremented){
                        oprns += diff-incremented;
                        incremented = diff;
                    }
                    else if(diff<incremented){
                        incremented = diff;
                    }
                }
                else{ //decrement
                    incremented = 0;
                    int diff = nums[i] - target[i];
                    if(diff>decremented){
                        oprns += diff - decremented;
                        decremented = diff;
                    }
                    else if(diff<decremented){
                        decremented = diff;
                    }
                }
            }
            else{
                incremented = 0;
                decremented = 0;
            }
        }
        return oprns;
    }
};
