class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<pair<int,int>> v;
        vector<pair<int,int>> p;
        for(int i=0;i<target.size();i++){
            int a = target[i] - nums[i];
            int b = 0;
            if(a <0){
                a = (-a);
                b = 1;
            }
            p.push_back({a,b});
        }

        long long a = 0;
        v.push_back(p[0]);
        int k = p[0].second;
        for (int i = 1; i < target.size(); i++) {
            if (p[i].first > v.back().first && p[i].second==k) {
                v.push_back(p[i]);
                continue;
            } 
            if (p[i].first == v.back().first && p[i].second==k)
                continue;

            if(v.empty()){
                v.push_back(p[i]);
                k=p[i].second;
                continue;
            }
            // cout<<"hi";
            int top = v.back().first;

            while (!v.empty() && p[i].first < v.back().first && v.back().second == p[i].second)
                v.pop_back();
            if(k != p[i].second){
                a+=top;
            }else{
                a += top - p[i].first;
            }
            v.push_back(p[i]);
            k=p[i].second;
            // cout<<a;
        }
        cout<<a;
        if (!v.empty()){
            a += v.back().first;
            // cout<<a;
        }

        return a;
    }
};
