class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diffs(nums);
        for (int i = 0; i < n; i++) diffs[i] = target[i] - nums[i];
        int now = 0;
        long long ret = 0;
        for (int diff: diffs) {
            if ((diff > 0 && now <= 0) || (diff < 0 && now >= 0)) {
                ret += abs(diff);
            } else if (diff > 0 && now > 0) {
                if (diff > now) {
                    ret += diff-now;
                } 
            } else if (diff < 0 && now < 0) {
                if (diff < now) {
                    ret += now - diff;
                }
            }
            now = diff;
        }
        return ret;

    }
};
