class Solution {
public:
long long ch(vector<long long int>&diff){
     int ans = 0;
     int n = diff.size();
        int curr = 0;

        for (int i = 0; i < n; ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int additional_operations = abs(diff[i]) - abs(curr);
                if (additional_operations > 0) {
                    ans += additional_operations;
                }
                curr = diff[i];
            } 
        }

        return ans;
}
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<long long > inc, dec;
        for(int i = 0; i< n; i++){
            if(nums[i] < target[i]){
                inc.push_back(target[i]- nums[i]);
                 ans += ch(dec);
                 dec.clear();

            }
            else if(nums[i] > target[i]){
               dec.push_back(nums[i] - target[i]);

               // increment wale ko solve karo 
               ans += ch(inc);
               inc.clear();
            }
            else{
            // dono ko solve karo..
          if(inc.size() > 0){
                ans += ch(inc);
                inc.clear();
          }
          if(dec.size() > 0){
                  ans += ch(dec);
                  dec.clear();
          }
            }
        }

        if(inc.size() > 0){
               ans += ch(inc);
               inc.clear();
          }
          if(dec.size() > 0){
                ans +=  ch(dec);
                dec.clear();
          }
        return ans;
    }
};
