class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        int t[n];
        for(int i=0;i<n;i++){
            t[i]=target[i]-nums[i];
        }
        int p=0;
        long long m=abs(t[0]),res=abs(t[0]);
        if(t[0]>=0){
            p=1;
        }
        for(int i=1;i<n;i++){
            if(t[i]<0 && p==1){
                res+=abs(t[i]);
                m=abs(t[i]);
                p=0;
                continue;
            }
            if(t[i]>=0 && p==0){
                res+=abs(t[i]);
                m=abs(t[i]);
                p=1;
                continue;
            }
            if(m>=abs(t[i])){
                m=abs(t[i]);
                continue;
            }
            else{
                res+=abs(t[i])-m;
                m=abs(t[i]);
            }
        }
        return res;
    }
};
