class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        tmp = []
        for i in range(n):
            tmp.append(nums[i] - target[i])
        dp = [0]*n
        dp[0] = tmp[0]
        ans = abs(tmp[0])
        # print(tmp)
        for i in range(1,n):
            x = 0
            if dp[i-1]*tmp[i]>0:
                if abs(dp[i-1]) < abs(tmp[i]):
                    dp[i] = tmp[i]
                    x = abs(tmp[i])-abs(dp[i-1])
                else:
                    dp[i] = tmp[i]
            else:
                dp[i] = tmp[i]
                x = abs(tmp[i])
            ans += x
            # print(dp[i-1],tmp[i], x, ans)
        return ans
