class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        long long ans=0;
        int n=nums.size();
        for(int i=0 ; i<n ; i++){
            if(nums[i]>=target[i]){
                int j=i;
                int prev=0;
                while(j<n && nums[j]>=target[j]){
                    long long diff=nums[j]-target[j];
                    if(diff>prev)ans+=diff-prev;
                    prev=diff;
                    j++;
                }
                i=j-1;
            }
            else{
                int j=i;
                int prev=0;
                while(j<n && nums[j]<target[j]){
                    long long diff=target[j]-nums[j];
                    if(diff>prev)ans+=diff-prev;
                    prev=diff;
                    j++;
                }
                i=j-1;
            }

        }
        return ans;
    }
};
