class Solution {
public:
    long long fun(vector<long long> &v){
        long long n=v.size();
        for(int i=0;i<n;i++) v[i]=abs(v[i]);
        long long cnt=v[0];
        for(int i=1;i<n;i++){
            if(v[i]>v[i-1]) cnt+=(v[i]-v[i-1]);
        }
        return cnt;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n=nums.size(),ans=0;
        vector<long long> req(n);
        for(int i=0;i<n;i++){
            req[i]=nums[i]-target[i];
        }
        vector<long long> temp;
        for(int i=0;i<n;i++){
            if(temp.size()){
                long long x=temp.back()*req[i];
                if(x<0){
                    ans+=fun(temp);
                    temp.clear();
                }
            }
            temp.push_back(req[i]);
        }
        if(temp.size()) ans+=fun(temp);
        return ans;
    }
};
