class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = [t - n for t, n in zip(target, nums)]
        ops = 0
        seg_diff = 0
        for diff in diffs:
            if (not diffs or (diff > 0) == (seg_diff > 0)):
                ops += max(0, abs(diff) - abs(seg_diff))
                seg_diff = diff
            else:
                ops += abs(diff)
                seg_diff = diff
        return ops
