using ll=long long;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> dif;
        int n=nums.size();
        for (int i=0; i<n; i++)
            dif.push_back(target[i]-nums[i]);
        // for (auto c: dif) cout << c << " "; cout << endl;
        vector<vector<int>> arr;
        vector<int> brr;
        for (auto c: dif){
            if (brr.size()==0)
                brr.push_back(c);
            else{
                if (!((brr.back()>=0) ^ (c<0))){
                    arr.push_back(brr);
                    if (c==0)
                        brr={};
                    else
                        brr={c};
                }
                else{
                    brr.push_back(c);
                }
            }
        }
        if (brr.size()>0)
            arr.push_back(brr);
        ll ret=0;
        for (auto c: arr){
            ret+=cal(c);
        }

        return ret;
    }
    ll cal(vector<int>& arr){
        int n=arr.size();
        if (arr[0]<0)
            for (auto& c: arr) c*=-1;
        ll cnt=arr[0];
        int lst=arr[0];
        for (int i=1; i<n; i++){
            if (arr[i]>lst){
                cnt+=arr[i]-lst;
                lst=arr[i];
            }
            else if (arr[i]==lst)
                continue;
            else{
                lst=arr[i];
            }
        }
        return cnt;
    }
};
