class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {


        int earl = 0;
        int vari = 0;
        int fir = 0;
        long long res = 0;
        int i=0;

        while(i<nums.size()) {
            if (earl == 0) {
                if (nums[i] < target[i]) {
                    earl = 1;
                    vari = target[i] - nums[i];
                    fir += 1;
                } else if (nums[i] > target[i]) {
                    earl = -1;
                    vari = nums[i] - target[i];
                    fir += 1;
                } else {
                    i++;
                    continue;
                }
            } else {
                if ((nums[i] < target[i] && earl == 1 && vari == target[i] - nums[i]) ||
                    (nums[i] > target[i] && earl == -1 && vari == nums[i] - target[i])) {
                    fir += 1;
                    i++;
                    continue;
                } else if (nums[i] < target[i] && earl == 1) {
                    if (target[i] - nums[i] > vari) {
                        fir = 1;
                        vari = target[i] - nums[i];
                    } else {
                        res += (vari - target[i] + nums[i]);
                        vari = target[i] - nums[i];
                    }
                    i++;
                    continue;
                } else if (nums[i] > target[i] && earl == -1) {
                    if (nums[i] - target[i] > vari) {
                        fir = 1;
                        vari = nums[i] - target[i];
                    } else {
                        res += (vari + target[i] - nums[i]);
                        vari = nums[i] - target[i];
                    }
                    i++;
                    continue;
                } else if (nums[i] == target[i]) {
                    res += vari;
                    earl = 0;
                    fir = 0;
                    vari = 0;
                } else {
                    res += vari;
                    if (nums[i] < target[i]) {
                        earl = 1;
                        vari = target[i] - nums[i];
                        fir += 1;
                    } else {
                        earl = -1;
                        vari = nums[i] - target[i];
                        fir += 1;
                    }
                }
            }
            i++;
        }

        return res + vari;



    }
};
