class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        int n = nums.size();
        vector<int> temp(n);
        for (int i = 0; i < n; ++i) {
            temp[i] = tar[i] - nums[i];
        }

        long long ans = 0;
        int curr = 0;

        for (int i = 0; i < n; ++i) {
            if (i == 0 or (temp[i - 1] > 0 and temp[i] > 0) or (temp[i - 1] < 0 and temp[i] < 0)) {
                int extra = abs(temp[i]) - abs(curr);
                if (extra > 0) {
                    ans += extra;
                }
                curr = temp[i];
            } else {
                ans += abs(temp[i]);
                curr = temp[i];
            }
        }

        return ans;   
    }
};
