class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        diff = [ x-y for x,y in zip(target,nums)]

        hpos =  [] 
        seen = set()
        hneg = []

        ans  =0 
        for i,y in enumerate(diff):
            if y>=0:
                heappush(hpos,(-y,i))
            if y<0:
                heappush(hneg,(y,i))

        while hpos:
            curr  , index  = heappop(hpos)
            curr =  diff[index]
            if index in seen:
                continue
            ans+=curr
            seen.add(index)
            for x in range(index+1,len(nums)):
                if diff[x] > 0 and  x not in seen:
                    curr=min(diff[x],curr)
                    diff[x]=diff[x]-curr
                    if diff[x]==0:
                        seen.add(x)    
                else:
                    break
            curr=diff[index]
            diff[index]=0
            for x in range(index-1,-1,-1):
                if diff[x] > 0 and  x not in seen:
                    curr=min(diff[x],curr)
                    diff[x]=diff[x]-curr
                    if diff[x]==0:
                        seen.add(x)    
                else:
                    break
