class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff1, diff2;
        int n = nums.size();
        long long res = 0;
        vector<int> diff;
        int last = 0;
        for (int i = 0; i < n; i++) {
            int temp = target[i] - nums[i];
            if (last < 0 && temp < 0 || last > 0 && temp > 0) {
                diff.push_back(abs(temp));
            } else {
                res += cal(diff);
                diff.clear();
                last = temp;
                diff.push_back(abs(temp));
            }
        }
        res += cal(diff);
        return res;
    }

    long long cal(vector<int>& v) {
        int last = 0;
        long long res = 0;
        for (int i = 0; i < v.size(); i++) {
            if (last < v[i]) {
                res += v[i] - last;
            }
            last = v[i];
        }
        return res;
    }   
};
