 class Solution {
public:
    #define ll long long
    bool same(int x, int y) {
        return ((x >= 0 && y >= 0) || (x < 0 && y < 0));
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        ll ans = 0;
        vector<int> diff;
        for (int i = 0; i < n; i++) {
            diff.push_back(target[i] - nums[i]);
        }

        vector<pair<int, int>> ranges;
        int u = 0, v;
        for (int i = 1; i < n; i++) {
            if (!same(diff[i-1], diff[i])) {
                v = i - 1;
                ranges.push_back({u, v});
                u = i;
            }
        }
        ranges.push_back({u, n - 1});

        for (auto it : ranges) {
            int u = it.first, v = it.second;
            if (diff[u] < 0) {
                for (int i = u; i <= v; i++) {
                    diff[i] *= -1;
                }
            }

            // for positive differences
            ans += diff[u];
            for (int i = u + 1; i <= v; i++) {
                if (diff[i] > diff[i-1]) {
                    ans += (diff[i] - diff[i-1]);
                }
            }
        }

        return ans;
    }
};
