class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> dif(n);
        for (int i = 0; i < n; i++) {
            dif[i] = target[i] - nums[i];
        }

        long long ans = 0;
        long long s = 0;

        for (int i = 0; i < dif.size(); i++) {
            if (i == 0 or (dif[i] > 0 and dif[i - 1] > 0) or
                (dif[i] < 0 and dif[i - 1] < 0)) {
                int temp = abs(dif[i]) - abs(s);
                if (temp > 0) {
                    ans += temp;
                }
                s = dif[i];
            } else {
                ans += abs(dif[i]);
                s = dif[i];
            }
        }

        return ans;
    }
};
