class Solution {
public:
    long long minimumOperations(vector<int>& s, vector<int>& t) {
        int flag=0;
        if((s[0]-t[0])<0){
            flag=1;
        }
        vector<vector<int>>v;
        vector<int>ti;
        for(int i=0;i<s.size();i++){
            int mid=0;
            if((s[i]-t[i])<0){
                mid=1;
            }
            if(flag==mid){
                ti.push_back(abs(s[i]-t[i]));
            }
            else{
                flag=mid;
                v.push_back(ti);
                ti.clear();
                ti.push_back(abs(s[i]-t[i]));                
            }
        }
        v.push_back(ti);
        long long int ans=0;
        for(auto &i:v){
            int last=0;
            for(auto &j:i){
                if(last<j){
                    ans+=(1ll*(j-last));
                }
                last=j;
            }

        }
        return ans;


    }
};
