class Solution:
    def minimumOperations(self, nums: List[int], t: List[int]) -> int:
        curr,ans=0,0
        for i in range(len(nums)):
            if (curr>0 and t[i]-nums[i]>0):
                temp=max(t[i]-(nums[i]+curr),0)
                curr=t[i]-nums[i]
            elif (curr<0 and t[i]-nums[i]<0):
                temp=min(t[i]-(nums[i]+curr),0)
                curr=t[i]-nums[i]
            else: 
                temp=t[i]-nums[i]
                curr=temp

            # print(i,temp,curr)
            ans+=abs(temp)
        return ans
