class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = n - 1; i; i--) {
            nums[i] -= nums[i - 1];
            target[i] -= target[i - 1];
        }
        long long add = 0, sub = 0, ans = 0;
        for (int i = 0; i < n; i++) {
            if (nums[i] >= target[i]) sub += nums[i] - target[i];
            else add += target[i] - nums[i];
        }
        return max(sub, add);
    }
};
