class Solution
{
public:
    long long minimumOperations(vector<int> &nums, vector<int> &target)
    {
        int n = nums.size();
        long long ans = 0, curr_d = 0;
        vector<int> cache(n, 0);
        for (int i = 0; i < n; ++i)
            cache[i] = nums[i] - target[i];
        for (int i = 0; i < n; ++i)
        {
            if (i == 0 || (cache[i - 1] > 0 && cache[i] > 0) || (cache[i - 1] < 0 && cache[i] < 0))
            {
                if ((abs(cache[i]) - abs(curr_d)) > 0)
                    ans += abs(cache[i]) - abs(curr_d);
            }
            else
                ans += abs(cache[i]);
            curr_d = cache[i];
        }
        return ans;
    }
};
