class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(target)
        target = [(target[i] - nums[i]) for i in range(n)]
        h = 0
        ans = 0
        for i in range(n):
            if target[i] > 0:
                if h < 0:
                    h = 0
                if target[i] > h:
                    ans += target[i] - h
                h = target[i]
            elif target[i] == 0:
                h = 0
                continue
            else:
                if h > 0:
                    h = 0
                if target[i] < h:
                    ans += h - target[i]
                h = target[i]
        return ans
