class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long [] a=new long[nums.length];

        for(int i=0;i<nums.length;i++){
            a[i]=(target[i]-nums[i]);
        }

        long prev=0;
        long cnt=0;
        long cnt1=0;
        for(int i=0;i<nums.length;i++){
            if(a[i]<=0) {
                prev=0;
                continue;
            }
            if( a[i]>prev){
                cnt+=a[i]-prev;
                prev=a[i];
            }
            else{
                prev=a[i];
            }
        }

        prev=0;

        for(int i=0;i<nums.length;i++){

                    if(a[i]>=0){
                        prev=0;
                        continue;
                    }
            a[i]=Math.abs(a[i]);

            if(a[i]<=0) {
                prev=0;
                continue;
            }
            if( a[i]>prev){
                cnt1+=a[i]-prev;
                prev=a[i];
            }
            else{
                prev=a[i];
            }
        }

     return cnt1+cnt;   
    }
}
