class Solution {
public:
    bool f(int a, int b) {
        return (a > 0 and b > 0) or (a < 0 and b < 0);
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n = nums.size(), ans = 0;
        vector<int> diff(n,0);
        for (int i = 0; i<n; i++) {
            diff[i] = target[i] - nums[i];
        }
        bool pos = (diff[0] > 0);
        int val = 0;
        int i = 0;
        while (i < n) {
            int j = i+1;
            while (j < n and f(diff[i],diff[j])) j++;
            j--;
            int prev = 0;
            for (i; i<=j; i++) {
                if (abs(diff[i]) > prev) ans += abs(diff[i]) - prev;
                prev = abs(diff[i]);
            }
        }
        return ans;
    }
};
