class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n=nums.size();
        vector<long long> t(n);
        for (int i=0; i<nums.size(); i++){
            t[i]=target[i] - nums[i];
        }
        long long cur = t[0];
        for (int i=1; i<n; i++){
            if ( (t[i] >=0 && cur < 0) || (t[i] < 0 && cur >= 0) ){
                ans+=abs(cur);
                cur = t[i];
            }
            else{
                if (cur < 0){
                    if (t[i] < cur) cur = t[i];
                    else{
                        ans+=abs(cur-t[i]);
                        cur = t[i];
                    }
                }
                else{
                    if (t[i] > cur) cur = t[i];
                    else{
                        ans+=abs(cur-t[i]);
                        cur = t[i];
                    }
                }}}
        ans+=abs(cur);
        return ans;
    }
};

