class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n = nums.size();
        vector<long long> diffrence(n);
        for(int i=0; i<n; i++){
            diffrence[i] = static_cast<long long>(target[i])- static_cast<long long> (nums[i]);
        }

        long long total = 0;
        long long current_diff = 0;

        for(int i=0; i<n; i++){
            if(i == 0 || (diffrence[i-1]>0 && diffrence[i]>0) || (diffrence[i-1]<0 && diffrence[i]<0)){
                long long additional_opr = abs(diffrence[i])-abs(current_diff);
                if(additional_opr>0){
                total += additional_opr;
            }
                current_diff = diffrence[i];
            }else{
                total += abs(diffrence[i]);
                current_diff = diffrence[i];
            }

        }
        return total;

    }
};
