class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long res = 0, prev  = 0;
        for (int i = 0; i < n; ++i) {
            int diff = target[i] - nums[i];
            if (i == 0 or (diff > 0 and prev > 0) or (diff < 0 and prev < 0))  res += max(0LL, abs(diff) - abs(prev));
            else res += abs(diff);
            prev = diff;
        }

        return res;
    }
};
