class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> arr(n);
        for(int i=0; i<n; i++){
            arr[i] = target[i] - nums[i];
        }

        long long count = abs(arr[0]);
        int prev = arr[0];
        for(int i=1; i<n; i++){
            if((arr[i]>0 && prev<=0) || (arr[i]<0 && prev>=0)){
                count += abs(arr[i]);
            }
            else{
                int temp = min(abs(arr[i]),abs(prev));
                count += abs(arr[i])-abs(temp);
            }
            prev = arr[i];
        }
        return count;
    }
};
