class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
     long long n = nums.size();
    vector<long long> div(n);

    // Constructing the difference array
    for (long long i = 0; i < n; ++i) {
        div[i] = target[i] - nums[i];
    }

    long long total = 0;
    long long curdif = 0;

    for (long long i = 0; i < n; ++i) {
        if (i == 0 || (div[i - 1] > 0 && div[i] > 0) || (div[i - 1] < 0 && div[i] < 0)) {
            long long exopt = std::abs(div[i]) - std::abs(curdif);
            if (exopt > 0) {
                total += exopt;
            }
            curdif = div[i];
        } else {
            total += std::abs(div[i]);
            curdif = div[i];
        }
    }

    return total;

    }
};
