class Solution {
public:
    int m = INT_MAX;
    long long solve(vector<int>& arr) {
        long long ans = 0;
        int maxi = 0;
        int prev = 0;
        int n = arr.size();
        for(int i = 0;i < n;i++) {
            if(arr[i] == m) {
                // cout<<" First  "<<maxi<<endl;
                ans += maxi - prev;
                maxi = 0;
                prev = 0;
            }
            else if(arr[i] < maxi) {
                ans += maxi - prev;
                // cout<<" Second  "<<(maxi - prev)<<endl;
                maxi = arr[i];
                prev = arr[i];
            }
            else {
                maxi = max(maxi, arr[i]);
            }
        }
        // cout<<ans<<endl;
        // cout<<endl;
        return ans;
    }

    void print(vector<int>& arr) {
        for(int i = 0;i < arr.size();i++)
            cout<<arr[i]<<" ";
        cout<<endl;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> arr(n,0);
        vector<int> pos;
        vector<int> neg;
        for(int i = 0;i < n;i++) {
            int a = target[i] - nums[i];
            if(a < 0) {
                neg.push_back(abs(a));
                pos.push_back(m);
            }
            else if(a == 0) {
                neg.push_back(m);
                pos.push_back(m);
            }
            else {
                neg.push_back(m);
                pos.push_back(a);
            }
        }
        neg.push_back(m);
        pos.push_back(m);
        // print(pos);
        // print(neg);

        ans += solve(pos) + solve(neg);
        return ans;
    }
};
