class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long prev = 0;
        long long diff = 0;
        long long st = 0;
        long long ans = 0;
        int n = nums.size();

        for (int i = 0; i < n; ++i) {
            if (prev == 0) {
                if (nums[i] < target[i]) {
                    prev = 1;
                    diff = target[i] - nums[i];
                    st += 1;
                } else if (nums[i] > target[i]) {
                    prev = -1;
                    diff = nums[i] - target[i];
                    st += 1;
                } else {
                    continue;
                }
            } else {
                if ((nums[i] < target[i] && prev == 1 && diff == target[i] - nums[i]) ||
                    (nums[i] > target[i] && prev == -1 && diff == nums[i] - target[i])) {
                    st += 1;
                    continue;
                } else if (nums[i] < target[i] && prev == 1) {
                    if (target[i] - nums[i] > diff) {
                        st = 1;
                        diff = target[i] - nums[i];
                    } else {
                        ans += (diff - target[i] + nums[i]);
                        diff = target[i] - nums[i];
                    }
                    continue;
                } else if (nums[i] > target[i] && prev == -1) {
                    if (nums[i] - target[i] > diff) {
                        st = 1;
                        diff = nums[i] - target[i];
                    } else {
                        ans += (diff + target[i] - nums[i]);
                        diff = nums[i] - target[i];
                    }
                    continue;
                } else if (nums[i] == target[i]) {
                    ans += diff;
                    prev = 0;
                    st = 0;
                    diff = 0;
                } else {
                    ans += diff;
                    if (nums[i] < target[i]) {
                        prev = 1;
                        diff = target[i] - nums[i];
                        st += 1;
                    } else {
                        prev = -1;
                        diff = nums[i] - target[i];
                        st += 1;
                    }
                }
            }
        }

        return ans + diff;
    }
};
