class Solution {
public:
    long long minimumOperations(vector<int>& alpha, vector<int>& beta) {
          int lengthSIZETOTAL = alpha.size();
        vector<int> VECTKRARRAYHEHAII(lengthSIZETOTAL);

        for (int i = 0; i < lengthSIZETOTAL; ++i) {
            VECTKRARRAYHEHAII[i] = beta[i] - alpha[i];
        }

        long long tot = 0;
        long long  difference = 0;

        for (int i = 0; i < lengthSIZETOTAL; ++i) {
            if (i == 0 || (VECTKRARRAYHEHAII[i - 1] > 0 && VECTKRARRAYHEHAII[i] > 0) || (VECTKRARRAYHEHAII[i - 1] < 0 && VECTKRARRAYHEHAII[i] < 0)) {
                int additionalOps = abs(VECTKRARRAYHEHAII[i]) - abs(difference);
                if (additionalOps > 0) {
                    tot += additionalOps;
                }
                difference = VECTKRARRAYHEHAII[i];
            } else {
                tot += abs(VECTKRARRAYHEHAII[i]);
                difference = VECTKRARRAYHEHAII[i];
            }
        }

        return tot;
    }
};
