class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        nums = [nums[i] - target[i] for i in range(len(nums))]
        prev = None
        ans = 0
        for num in nums:
            if prev is None:
                ans += abs(num)
                prev = num
                continue
            if prev > 0:
                if (diff := num - prev) > 0:
                    ans += diff
                    prev = num
                elif num < 0:
                    ans += -num
                    prev = num
                else:
                    prev = num
            else:
                if (diff := num - prev) < 0:
                    ans -= diff
                    prev = num
                elif num > 0:
                    ans += num
                    prev = num
                else:
                    prev = num
        return ans
