class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        int n = nums.size();
        int last = 0;
        for (int i = 0; i < n; i++) {
            int delta = target[i] - nums[i];
            if (delta > last) res += delta-last;
            last = delta;
        }
        if (last < 0) res += abs(last);
        return res;
    }
};
